import React from 'react';
import './App.css';

class Container extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render(){
        return (
            <div>
                <AppHeader/>
                <AppMainContent/>
            </div>
        )
    }

}

class AppHeader extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render(){
        return (
            <menu></menu>
        )
    }

}

class MenuItem extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    addItem(event){
        console.log(event.target.attributes.code.value);
    }

    render(){
        return (
            <button className="MenuItem" onClick={this.addItem} code={this.props.code}>{this.props.name} <br/> {this.props.fishes} <br/> {this.props.count}Pz <br/>
                <img src={this.props.img} className="itemLogo" alt={this.props.name}/></button>

        )
    }
}

class AppMainContent extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            items: [{
                name: "Nigiri",
                fishes: ["Salmon"],
                count: 2,
                img: "https://www.shinto.it/wp-content/uploads/2017/02/nigiri-salmone-big.jpg",
                code : 67
            },
                {
                    name: "Hosomaki",
                    fishes: ["salmon"],
                    count: 6,
                    img: "http://shibuyaonline.it/image/cache/old/Nigiri-Tonno-800x800.jpg",
                    code : 67
                },
                {
                    name: "Uramaki",
                    fishes: ["salmon"],
                    count: 8,
                    img: "https://www.oh-sushi.it/wp-content/uploads/2019/01/uramaki-salmone-avocado-tobiko-oh-sushi-firenze-giapponese-600x600.jpg",
                    code : 67
                },
                {
                    name: "Temaki",
                    fishes: ["salmon"],
                    count: 8,
                    img: "https://www.oh-sushi.it/wp-content/uploads/2019/01/temaki-salmone-avocado-oh-sushi-firenze-giapponese-600x600.jpg",
                    code : 67
                },
                {
                    name: "Futomaki",
                    fishes: ["salmon"],
                    count: 8,
                    img: "https://www.harusushi.it/wp-content/uploads/2014/06/futomaki_salmone_2.jpg",
                    code : 67
                },
                {
                    name: "Gunkan",
                    fishes: ["salmon"],
                    count: 8,
                    img: "https://www.darumasushi.com/wp-content/uploads/2017/09/gunkan-salmon-philadelphia-daruma-sushi.jpg",
                    code : 67
                },
                {
                    name: "Gunkan",
                    fishes: ["salmon"],
                    count: 8,
                    img: "https://www.darumasushi.com/wp-content/uploads/2017/09/gunkan-salmon-philadelphia-daruma-sushi.jpg",
                    code : 67
                },

            ]
        }
    }

    componentDidMount() {
        fetch("http://localhost:9000/menu")
            .then(res => res.json())
            .then(res => this.setState({ items: res }));
        console.log(JSON.stringify(this.state.items));
    }

    render(){
        return (
            <div className="AppMainContent">
                {this.state.items.map(item => {
                    return <MenuItem name={item.name} fishes={item.fishes.concat()} count={item.count} img={item.img} code={item.code}/>
                })}
                <br/>

            </div>
        )
    }
}

class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }
    render(){
        return (
            <Container/>
        )
    }
}

export default App;
