import React from 'react';
import './App.css';

const ServerIP = "http://172.29.0.168:9000/"

class Container extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render(){
        return (
            <>
                <AppHeader/>
                <div className="UpperSeparator"/>
                <AppMainContent/>
                <div className="BottomSeparator"/>
            </>
        )
    }

}

class AppHeader extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    resetOrder(){
        fetch(ServerIP+"order/reset/", {
            method: 'POST',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
        }).then(res => res.json()).then(res => console.log(res));
    }

    render(){
        return (
            <div className="AppHeader">
                TemakiOrder
            </div>
        )
    }
}

class MenuItem extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            // orderedItems: 0,
        };
        this.removeItem = this.removeItem.bind(this);
        this.addItem = this.addItem.bind(this);
    }

    removeItem(event){
        var code;
        if(event.target.attributes.code !== undefined){
            code = event.target.attributes.code;
        }else{
            code = event.target.parentNode.attributes.code;
        }
        this.changeItem(code, -1);
    }

    addItem(event){
        var code;
        if(event.target.attributes.code !== undefined){
            code = event.target.attributes.code;
        }else{
            code = event.target.parentNode.attributes.code;
        }
        this.changeItem(code, +1);
    }

    changeItem(code, sign){
        console.log(code.value);
        this.props.order(code.value, sign);

    }

    render(){
        return (
            <div className="MenuItem" code={this.props.code}>{this.props.name} <br/> {this.props.count}Pz <br/>
                <button className="RemoveItem" onClick={this.removeItem}>-</button>
                <img src={this.props.img} className="itemLogo" alt={this.props.name}/>
                <button className="AddItem" onClick={this.addItem}>+</button>
                <div className="ItemCounter">{this.props.selected}</div>
            </div>
        )
    }
}

class MyOrder extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            mode: "OrderClosed",
        };
        this.openClose = this.openClose.bind(this);
    }

    openClose(){
        if(this.state.mode === "OrderClosed"){
            this.setState({
                mode: "OrderOpened",
            })
        }else if(this.state.mode === "OrderOpened"){
            this.setState({
                mode: "OrderClosed",
            })
        }
    }

    render(){
        return (
            <div className={this.state.mode}>
                <div className="MyOrderTitle">
                    Il tuo ordine
                </div>
                <ul className="MyOrderList">
                    {this.props.selectedItems.map((item) => {
                        return  <li>{item[1]}  x{item[0]} - {this.props.menu[item[1]-1].name}</li>
                    })}
                </ul>
                <button className="OpenMenu" onClick={this.openClose}>OP</button>
            </div>
        )
    }
}

class CompleteOrder extends React.Component{
    constructor(props){
        super(props);
        this.setState({

        })
    }

    render(){
        return (
            <div></div>
        );
    }
}

class OrderBar extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            myMode : "",
            completeMode: "",
        }
    }

    toggleMyOrder(){
        if(this.state.completeMode === "OrderOpened"){
            this.setState({
                completeMode: "OrderClosed"
            })
        }

        if(this.state.myMode === "OrderOpened"){
            this.setState({
                myMode: "OrderClosed"
            })
        }else if(this.state.myMode === "OrderClosed"){
            this.setState({
                myMode: "OrderOpened"
            })
        }
    }

    toggleCompleteOrder(){
        if(this.state.myMode === "OrderOpened"){
            this.setState({
                myMode: "OrderClosed"
            })
        }

        if(this.state.completeMode === "OrderOpened"){
            this.setState({
                completeMode: "OrderClosed"
            })
        }else if(this.state.completeMode === "OrderClosed"){
            this.setState({
                completeMode: "OrderOpened"
            })
        }

    }

    render() {
        return (
            <div className="OrderBar">
                <MyOrder mode={this.state.myMode} toogle={this.toggleMyOrder.bind(this)}/>
                <CompleteOrder mode={this.state.completeMode} toogle={this.toggleCompleteOrder.bind(this)}/>
            </div>
        );
    }
}

class AppMainContent extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            mode: "login",
            user: "",
            items: [],
            orders: new Array(100),
            completeOrders: new Array(100),
            orderByUser: new Array(100),
        };
        this.state.orders.fill(0);
        this.sendOrder = this.sendOrder.bind(this);
        this.login = this.login.bind(this);
        this.setUserName = this.setUserName.bind(this);
        this.tryLogin = this.tryLogin.bind(this);
    }

    componentDidMount() {
        fetch(ServerIP+"menu")
            .then(res => res.json())
            .then(res => this.setState({ items: res }));
        //console.log(JSON.stringify(this.state.items));
    }

    updateOrder(itemCode, sign){
        let ordersUpdate = this.state.orders;
        if(ordersUpdate[itemCode] + sign>=0)
            ordersUpdate[itemCode] = ordersUpdate[itemCode] + sign;
        this.setState({
            orders: ordersUpdate,
        });
        console.log("stateInMain")
        console.log(this.state.orders);
    }

    sendOrder(){
        let order = {
            user: this.state.user,
            items: this.state.orders,
        };

        fetch(ServerIP+"order", {
            method: 'POST',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(order)
        }).then(res => res.json()).then(res => {
            this.setState({
                completeOrders: res.complete,
                orderByUser: res.byUser,
            });
            console.log(res);
        });

    }

    setUserName(e){
        this.setState({
            user: e.target.value,
        })
    }

    tryLogin(e){
        if(e.keyCode === 13){
            this.login()
        }
    }

    login(){
        //TODO: Fetch server and retrieve previous order of the user if any are present SOLVED
        fetch(ServerIP+"order/userMenu/", {
            method: 'POST',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({user:this.state.user}),
        })
            .then(res => res.json())
            .then(res => this.setState({ orders: res }));
        this.setState({
            mode: "menu"
        })
    }

    render(){
        let myOrder = this.state.orders.map((item, idx) => {
            return [item, idx];
        }).filter((item) => {
            return item[0]>0;
        });
        if(this.state.mode === "menu"){
            return (
                <div className="AppMainContent">
                    <button id="Send" onClick={this.sendOrder}>OK</button>
                    {/*TODO: Add sections of the menu*/}
                    {this.state.items.map((item, idx) => {
                        return <MenuItem name={item.name} ingredients={item.ingredients.concat()} count={item.count} img={item.img} code={item.code} order={this.updateOrder.bind(this)} selected={this.state.orders[idx+1]}/>
                    })}
                    <OrderBar selectedItems={myOrder} menu={this.state.items} complete={this.state.completeOrders} byUser={this.state.orderByUser}/>
                    {/*<MyOrder selectedItems={myOrder} menu={this.state.items}/>*/}
                </div>
            )
        }else if(this.state.mode === "login"){
            return (
                <div className="Login">
                    <input id="name" type="text" onChange={this.setUserName} onKeyDown={this.tryLogin}/>
                    <button id="loginButton" onClick={this.login}>OK</button>
                </div>

            )
        }
    }
}

class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }
    render(){
        return (
            <Container/>
        )
    }
}

export default App;
