import React from 'react';
import './App.css';
import './AppMainContent'
import AppMainContent from "./AppMainContent";

const ServerIP = "localhost:9000/"

class Container extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render(){
        return (
            <>
                <AppHeader/>
                <div className="UpperSeparator"/>
                <AppMainContent/>
                <div className="BottomSeparator"/>
            </>
        )
    }

}

class AppHeader extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    resetOrder(){
        fetch(ServerIP+"order/reset/", {
            method: 'POST',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
        }).then(res => res.json()).then(res => console.log(res));
    }

    render(){
        return (
            <div className="AppHeader">
                TemakiOrder
            </div>
        )
    }
}

class MenuItem extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            // orderedItems: 0,
        };
        this.removeItem = this.removeItem.bind(this);
        this.addItem = this.addItem.bind(this);
    }

    removeItem(event){
        var code;
        if(event.target.attributes.code !== undefined){
            code = event.target.attributes.code;
        }else{
            code = event.target.parentNode.attributes.code;
        }
        this.changeItem(code, -1);
    }

    addItem(event){
        var code;
        if(event.target.attributes.code !== undefined){
            code = event.target.attributes.code;
        }else{
            code = event.target.parentNode.attributes.code;
        }
        this.changeItem(code, +1);
    }

    changeItem(code, sign){
        console.log(code.value);
        this.props.order(code.value, sign);

    }

    render(){
        return (
            <div className="MenuItem" code={this.props.code}>{this.props.name} <br/> {this.props.count}Pz <br/>
                <button className="RemoveItem" onClick={this.removeItem}>-</button>
                <img src={this.props.img} className="itemLogo" alt={this.props.name}/>
                <button className="AddItem" onClick={this.addItem}>+</button>
                <div className="ItemCounter">{this.props.selected}</div>
            </div>
        )
    }
}

class MyOrder extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render(){
        return (
            <div className={"Order" + this.props.mode} id={"MyOrder" + this.props.mode}>
                <div className="OrderTitle">
                    Il tuo ordine
                </div>
                <ul className="OrderList">
                    {this.props.selectItems.map((item) => {
                        return  <li>{item[1]}  x{item[0]} - {this.props.menu[item[1]-1].name}</li>
                    })}
                </ul>
                <button className="OpenMenu" onClick={this.props.toggle}/>
            </div>
        )
    }
}

class CompleteOrder extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        };
    }

    render(){
        return (
            <div className={"Order" + this.props.mode} id={"CompleteOrder"+this.props.mode}>
                <div className="OrderTitle">
                    Ordine Tavolo
                </div>
                <ul className="OrderList">
                    {this.props.selectItems.map((item) => {
                        return  <li>{item[1]}  x{item[0]} - {this.props.menu[item[1]-1].name}</li>
                    })}
                </ul>
                <button className="OpenMenu" onClick={this.props.toggle}/>
            </div>
        );
    }
}

class OrderBar extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            myMode : "Closed",
            completeMode: "Closed",
        }
        this.toggleCompleteOrder = this.toggleCompleteOrder.bind(this);
        this.toggleMyOrder = this.toggleMyOrder.bind(this);
    }

    toggleMyOrder(){
        if(this.state.completeMode === "Opened"){
            this.setState({
                completeMode: "Closed"
            })
        }
        if(this.state.myMode === "Opened"){
            this.setState({
                myMode: "Closed"
            })
        }else if(this.state.myMode === "Closed"){
            this.setState({
                myMode: "Opened"
            })
        }
    }

    toggleCompleteOrder(){
        if(this.state.myMode === "Opened"){
            this.setState({
                myMode: "Closed"
            })
        }
        if(this.state.completeMode === "Opened"){
            this.setState({
                completeMode: "Closed"
            })
        }else if(this.state.completeMode === "Closed"){
            this.setState({
                completeMode: "Opened"
            })
        }

    }

    render() {
        return (
            <div className="OrderBar">
                <MyOrder mode={this.state.myMode} toggle={this.toggleMyOrder.bind(this)} selectItems={this.props.selectedItems} menu={this.props.menu}/>
                <CompleteOrder mode={this.state.completeMode} toggle={this.toggleCompleteOrder.bind(this)} selectItems={this.props.complete} menu={this.props.menu}/>
            </div>
        );
    }
}



class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }
    render(){
        return (
            <Container/>
        )
    }
}

export default App;
