import React from 'react';
import './App.css';

const ServerIP = "http://192.168.43.137:9000/"

class Container extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render(){
        return (
            <div>
                <AppHeader/>
                <div className="Separator"/>
                <AppMainContent/>
            </div>
        )
    }

}

class AppHeader extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    resetOrder(){
        fetch(ServerIP+"order/reset/", {
            method: 'POST',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
        }).then(res => res.json()).then(res => console.log(res));
    }

    render(){
        return (
            <div className="AppHeader">
                <button id="Reset" onClick={this.resetOrder}>R</button>TemakiOrder</div>
        )
    }

}

class MenuItem extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            orderedItems: 0,
        };
        this.removeItem = this.removeItem.bind(this);
        this.addItem = this.addItem.bind(this);
    }

    removeItem(event){
        var code;
        if(event.target.attributes.code !== undefined){
            code = event.target.attributes.code;
        }else{
            code = event.target.parentNode.attributes.code;
        }
        this.changeItem(code, -1);
    }

    addItem(event){
        var code;
        if(event.target.attributes.code !== undefined){
            code = event.target.attributes.code;
        }else{
            code = event.target.parentNode.attributes.code;
        }
        this.changeItem(code, +1);
    }

    changeItem(code, sign){
        console.log(code.value);
        var b = {
            code: code.value,
            change: sign
        };
        fetch(ServerIP+"order", {
            method: 'POST',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(b)
        }).then(res => res.json()).then(res => console.log(res));
        this.setState({
            orderedItems: this.state.orderedItems + sign,
        })
    }

    render(){
        return (
            <div className="MenuItem" code={this.props.code}>{this.props.name} <br/> {this.props.ingredients} <br/> {this.props.count}Pz <br/>
                <button className="RemoveItem" onClick={this.removeItem}>-</button>
                <img src={this.props.img} className="itemLogo" alt={this.props.name}/>
                <button className="AddItem" onClick={this.addItem}>+</button>
                <br/>
                {this.state.orderedItems}
            </div>
        )
    }
}

class AppMainContent extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            items: []
        }
    }

    componentDidMount() {
        fetch(ServerIP+"menu")
            .then(res => res.json())
            .then(res => this.setState({ items: res }));
        //console.log(JSON.stringify(this.state.items));
    }

    render(){
        return (
            <div className="AppMainContent">
                {this.state.items.map(item => {
                    return <MenuItem name={item.name} ingredients={item.ingredients.concat()} count={item.count} img={item.img} code={item.code}/>
                })}
                <br/>
            </div>
        )
    }
}

class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }
    render(){
        return (
            <Container/>
        )
    }
}

export default App;
