import React from 'react';
import './App.css';

class Container extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render(){
        return (
            <div>
                <AppHeader/>
                <div className="Separator"/>
                <AppMainContent/>
            </div>
        )
    }

}

class AppHeader extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render(){
        return (
            <div className="AppHeader">TemakiOrder</div>
        )
    }

}

class MenuItem extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    static addItem(event){
        var code;
        if(event.target.attributes.code !== undefined){
            code = event.target.attributes.code;
        }else{
            code = event.target.parentNode.attributes.code;
        }

        console.log(code.value);
        var b = {
            code: code.value
        };
        fetch("http://192.168.1.8:9000/order", {
            method: 'POST',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(b)
        })

    }

    render(){
        return (
            <button className="MenuItem" onClick={MenuItem.addItem} code={this.props.code}>{this.props.name} <br/> {this.props.ingredients} <br/> {this.props.count}Pz <br/>
                <img src={this.props.img} className="itemLogo" alt={this.props.name}/></button>
        )
    }
}

class AppMainContent extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            items: []
        }
    }

    componentDidMount() {
        fetch("http://192.168.1.8:9000/menu")
            .then(res => res.json())
            .then(res => this.setState({ items: res }));
        //console.log(JSON.stringify(this.state.items));
    }

    render(){
        return (
            <div className="AppMainContent">
                {this.state.items.map(item => {
                    return <MenuItem name={item.name} ingredients={item.ingredients.concat()} count={item.count} img={item.img} code={item.code}/>
                })}
                <br/>
            </div>
        )
    }
}

class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }
    render(){
        return (
            <Container/>
        )
    }
}

export default App;
